import pytest
import problem_5.problem_5_solution as p5s


@pytest.mark.parametrize(
    "given_string, expected",
    [
        ('s', True),
        ('babab', True),
        ('abaca', False),
        ('bb', True),
        ('bcb', True),
        ('ba', False),
        ('bac', False),
        ('', True),
    ]
)
def test_is_palindrome(given_string, expected):
    result = p5s.is_palindrome(given_string)
    assert result is expected


@pytest.mark.parametrize(
    "given_string, expected_palindrome",
    [
        ('s', 's'),
        ('babab', 'babab'),
        ('cadcbc', 'cbc'),
        ('abacabba', 'abba'),
        ('', ''),
    ]
)
def test_find_longest_palindrome_starting_with_last_value(given_string, expected_palindrome):
    result = p5s.find_longest_palindrome_starting_with_last_value(given_string)
    assert result == expected_palindrome


@pytest.mark.parametrize(
    "given_string, expected_palindrome",
    [
        ('s', 's'),
        ('babad', 'bab'),
        ('cbbd', 'bb'),
        ('abacabba', 'bacab'),
        ('', ''),
    ]
)
def test_problem_5_solution(given_string, expected_palindrome):
    result = p5s.longest_palindrome(given_string)
    assert result == expected_palindrome
